import React from 'react'
import { View, Text ,Button} from 'react-native'
import {useDispatch, useSelector} from 'react-redux'
import { setIncrement,setDecrement } from './redux/CounterAction'
const Counter = () => {
    const dispatch = useDispatch();
    const counter = useSelector(data => data.count);
    return (
        <View style={{flex:1,alignItems:'center',justifyContent:'center',flexDirection:'row'}}>
        
            <Button style={{backgroundColor:'#DDDDDD',paddingBottom:5,paddingTop:5,paddingLeft:20,paddingRight:20}} onPress={()=>dispatch(setIncrement(counter))}>
                <Text style={{fontSize:30}}>-</Text>
            </Button>
            <Text style={{fontSize:50,marginLeft:20,marginRight:20}}>{counter}</Text>
            <Button style={{backgroundColor:'#DDDDDD',paddingBottom:5,paddingTop:5,paddingLeft:20,paddingRight:20}} onPress={()=>dispatch(setDecrement(counter))}>
                <Text style={{fontSize:30}}>+</Text>
            </Button>
        
        </View>
    )
}

export default Counter
