import React ,{ useState } from 'react';
import { View, Text, StyleSheet,Button, TouchableOpacity, TextInput } from 'react-native';
import {useDispatch, useSelector} from 'react-redux'
import {setPlace} from './redux/placeAction'
const ListItem = () => {
    const [placeNam] = useState('');
    const dispatch = useDispatch();
    const placeName = useSelector(data => data.placeName )
    return (
      <TouchableOpacity>
        <View style = { styles.listItem }>
        <TextInput
          placeholder = "Seach Places"
          value ={placeNam}
        ></TextInput>
        <Button title = 'Add' onPress = { ()=>dispatch(setPlace()) }
        />
          <Text>{ placeName }</Text>
        </View>
      </TouchableOpacity>
    );
}

const styles = StyleSheet.create({
  listItem: {
    width: '100%',
    padding: 10,
    marginBottom: 10,
    backgroundColor: '#eee'
  }
});

export default ListItem;
