import React ,{ useState }from 'react';
import {FlatList,Text, Button} from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { deleteFood } from './redux/food';

const FoodList = () => {
  const [food, setFood] = useState('')
  const dispatch = useDispatch();
  const deleteCurrent = (key) => dispatch(deleteFood(key))
  const foods = useSelector(state => state.foodReducer.foodList)
  const submitFood = (food) => dispatch(addFood(food))
  console.log(foods)
  return (
    <View>
    <TextInput
      value={food}
      placeholder='Name'
      onChangeText={(food) => setFood(food)}
    />
    <Button
      style={{ marginBottom: 16 }}onPress={() => {
        submitFood(food) }} title="Submit"/>

    <FlatList 

      data={foods}
      keyExtractor={(item, index) => item.key.toString()}
      renderItem={({item}) => <Text>{item.name}<Button style={{marginLeft:100}} title="hapus" onPress={() => deleteCurrent(item.key)}></Button></Text>}

    />
    </View>
  );
}


export default FoodList;
